sp_angular_provider.script:
function () {

        var info =[];

        return {
            getInfo: getInfo,
            setInfo: setInfo,
			getStringIds:getStringIds
        };


        function getInfo() {
            return info;
        }

        function setInfo(value) {
            //info.push(value);
			info=value;
        }
	function getStringIds()
	{
		var str ='';		
		for(var i =0 ;  i < info.length; i ++)
			{				
				str = str + info[i] + ',';
			}
		console.log("str:" + info.toString());
		return str;

	}
}
